import{a as h}from"./chunk-CGH4GANM.js";import"./chunk-NCOWMHCX.js";import{a as g}from"./chunk-CDUCBZGU.js";import"./chunk-ELQQ62YC.js";import{a as t}from"./chunk-HBTGZLOF.js";import{L as d,Qa as i,Ra as p,Sb as x,ia as c,va as l}from"./chunk-H5ZZRFLE.js";import{a as o,b as r,g as w}from"./chunk-JPKLQMV2.js";var y=w(x());var v={title:"Create (Add) a Row",mdFile:"./index.md",order:3,category:h},s=v;var m=[];var A={},u=A;var C=`<h1 id="create-add-a-row" class="ngde">Create (Add) a Row<a title="Link to heading" class="ng-doc-header-link ngde" href="/using-smart-ng-rx/crud-support/create#create-add-a-row"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h1><p class="ngde">By now, you should be familiar with the structure of SmartNgRX rows. A row can have zero or more children. The children are represented by an <code class="ngde ng-doc-code-with-link" class="ngde"><a href="/api/smart-ngrx/classes/ArrayProxy" class="ng-doc-code-anchor ngde" data-link-type="Class" class="ngde">ArrayProxy</a></code> object. To add a child row, you need to first call the <code class="ngde ng-doc-code-with-link" class="ngde"><a href="/api/smart-ngrx/classes/ArrayProxy" class="ng-doc-code-anchor ngde" data-link-type="Class" class="ngde">ArrayProxy</a></code>'s <code class="ngde">addToStore</code> method, which you can access from the <code class="ngde ng-doc-code-with-link" class="ngde"><a href="/api/smart-ngrx/interfaces/SmartArray" class="ng-doc-code-anchor ngde" data-link-type="Interface" class="ngde">SmartArray</a></code> interface, passing it a dummy row you want to add, including a unique ID. This will add the row to the store and the ID to the array.</p><p class="ngde">Sample code from the demo app:</p><pre class="ngde hljs"><code class="hljs language-typescript code-lines ngde" lang="typescript" name="" icon="" highlightedlines="[]"><span class="line ngde"><span class="hljs-title function_ ngde">addChild</span>(<span class="hljs-attr ngde">row</span>: <span class="hljs-title class_ ngde">DepartmentChild</span>, <span class="hljs-attr ngde">parent</span>: <span class="hljs-title class_ ngde">TreeNode</span>): <span class="hljs-built_in ngde">void</span> {
</span><span class="line ngde">  <span class="hljs-keyword ngde">if</span> (parent.<span class="hljs-property ngde">isExpanded</span> === <span class="hljs-literal ngde">false</span>) {
</span><span class="line ngde">    <span class="hljs-variable language_ ngde">this</span>.<span class="hljs-title function_ ngde">toggleExpand</span>(parent);
</span><span class="line ngde">  }
</span><span class="line ngde">
</span><span class="line ngde">  parent.<span class="hljs-property ngde">node</span>.<span class="hljs-property ngde">children</span>.<span class="hljs-property ngde">addToStore</span>!(row, parent.<span class="hljs-property ngde">node</span>);
</span><span class="line ngde">}
</span></code></pre><p class="ngde">At this point, the row is in the store, and you can edit it like you would edit an already existing row. The only difference is that the row is not yet on the server.</p><p class="ngde">To save the row to the server, you update the row in the same way you would update any other row. The system is smart enough to know that the row is new and will ultimately call the <code class="ngde">add</code> method in the corresponding <code class="ngde ng-doc-code-with-link" class="ngde"><a href="/api/smart-ngrx/classes/EffectService" class="ng-doc-code-anchor ngde" data-link-type="Class" class="ngde">EffectService</a></code>.</p><p class="ngde">To remove a row that has not been saved to the server yet, you would call the <code class="ngde ng-doc-code-with-link" class="ngde"><a href="/api/smart-ngrx/classes/ArrayProxy" class="ng-doc-code-anchor ngde" data-link-type="Class" class="ngde">ArrayProxy</a></code>'s <code class="ngde">removeFromStore</code> method. This will remove the row from the store and the ID from the array.</p>`,P=(()=>{let e=class e extends t{constructor(){super(),this.routePrefix="",this.pageType="guide",this.pageContent=C,this.page=s,this.demoAssets=u}};e.\u0275fac=function(n){return new(n||e)},e.\u0275cmp=d({type:e,selectors:[["ng-doc-page-using-smart-ng-rx-crud-support-create"]],standalone:!0,features:[i([{provide:t,useExisting:e},m,s.providers??[]]),c,p],decls:1,vars:0,template:function(n,D){n&1&&l(0,"ng-doc-page")},dependencies:[g],encapsulation:2,changeDetection:0});let a=e;return a})(),j=[r(o({},(0,y.isRoute)(s.route)?s.route:{}),{path:"",component:P,title:"Create (Add) a Row"})],F=j;export{P as DynamicComponent,F as default};
