import{a as p}from"./chunk-65QNGUSK.js";import"./chunk-T4WX5D76.js";import"./chunk-3FR5PQZO.js";import{a as m}from"./chunk-CCBDXPMP.js";import{a as n}from"./chunk-LSIHCBJT.js";import{X as N}from"./chunk-CQLVC7LO.js";import"./chunk-PZEHRWLK.js";import{Qa as c,pb as g,ta as r,xa as d}from"./chunk-JNJDPWAB.js";import{a,b as i,h as D}from"./chunk-TWZW5B45.js";var u=D(N());var y={title:"Updating Fields",mdFile:"./index.md",order:8,category:m},t=y;var h=[];var w={},l=w;var b=`<h1 id="updating-fields" href="ngrx-demo-walkthrough/updating-fields" headinglink="true" class="ngde">Updating Fields<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="updating-fields"></ng-doc-heading-anchor></h1><p class="ngde">We trigger editing of the node labels by <a href="https://github.com/DaveMBush/SmartNgRX/blob/main/apps/demo-ngrx-classic/src/app/shared/components/tree/tree.component.html#L95" class="ngde">clicking on the edit icon</a> which triggers our <a href="https://github.com/DaveMBush/SmartNgRX/blob/main/apps/demo-ngrx-classic/src/app/shared/components/tree/tree.component.ts#L122-L125" class="ngde">editNode()</a> method in the tree.component.ts file.</p><p class="ngde">Notice that we save which node we are editing so that if a repaint occurs, we don't stop editing. We also save off the current content so that if we cancel out of the edit, we can set things back to the way they were.</p><p class="ngde">This then loads our edit component that we've created where we can make the changes. If we press <code class="ngde">ESC</code> while editing, it fires our <a href="https://github.com/DaveMBush/SmartNgRX/blob/main/apps/demo-ngrx-classic/src/app/shared/components/tree/tree.component.ts#L131-L133" class="ngde">cancelEdit()</a> method which sets everything back to the way it was.</p><p class="ngde">When we press <code class="ngde">ENTER</code>, it triggers our <a href="https://github.com/DaveMBush/SmartNgRX/blob/main/apps/demo-ngrx-classic/src/app/shared/components/tree/tree.component.ts#L135-L144" class="ngde">saveNode()</a> method. This undoes the edit state of our presentation and then stores the editingContent field into the node's name field. Updating the node's name field optimistically updates the field and eventually triggers the Effects Service to update the server.</p>`,e=class e extends n{constructor(){super();this.pageType="guide";this.pageContent=b;this.editSourceFileUrl="https://github.com/DaveMBush/SmartNgRX/edit/main/apps/documentation/src/app/ngrx-demo-walkthrough/updating-fields/index.md?message=docs(): describe your changes here...";this.viewSourceFileUrl="https://github.com/DaveMBush/SmartNgRX/blob/release/apps/documentation/src/app/ngrx-demo-walkthrough/updating-fields/index.md";this.page=t;this.demoAssets=l}};e.\u0275fac=function(o){return new(o||e)},e.\u0275cmp=r({type:e,selectors:[["ng-doc-page-00lptv6j"]],features:[g([{provide:n,useExisting:e},h,t.providers??[]]),d],decls:1,vars:0,template:function(o,P){o&1&&c(0,"ng-doc-page")},dependencies:[p],encapsulation:2,changeDetection:0});var s=e,v=[i(a({},(0,u.isRoute)(t.route)?t.route:{}),{path:"",component:s,title:"Updating Fields"})],I=v;export{s as PageComponent,I as default};
