import{a as m}from"./chunk-3VZM5QD4.js";import{a as g}from"./chunk-YWRD4FHU.js";import"./chunk-WDR7ZGDV.js";import{a as s}from"./chunk-RVP6FOET.js";import{L as r,Qa as p,Ra as h,Rb as N,ia as d,va as c}from"./chunk-ECT43PI4.js";import{a,b as i,g as v}from"./chunk-JPKLQMV2.js";var f=v(N());var b={title:"Updating Fields",mdFile:"./index.md",order:8,category:m},t=b;var l=[];var D={},u=D;var R=`<h1 id="updating-fields" class="ngde">Updating Fields<a title="Link to heading" class="ng-doc-header-link ngde" href="/demo-walkthrough/updating-fields#updating-fields"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h1><p class="ngde">We trigger editing of the node labels by <a href="https://github.com/DaveMBush/SmartNgRX/blob/main/apps/demo/src/app/shared/components/tree/tree.component.html#L89" class="ngde">clicking on the edit icon</a> which triggers our <a href="https://github.com/DaveMBush/SmartNgRX/blob/main/apps/demo/src/app/shared/components/tree/tree.component.ts#L85-L88" class="ngde">editNode()</a> method in the tree.component.ts file.</p><p class="ngde">Notice that we save which node we are editing so that if a repaint occurs, we don't stop editing. We also save off the current content so that if we cancel out of the edit, we can set things back to the way they were.</p><p class="ngde">This then loads our edit component that we've created where we can make the changes. If we press <code class="ngde">ESC</code> while editing, it fires our <a href="https://github.com/DaveMBush/SmartNgRX/blob/main/apps/demo/src/app/shared/components/tree/tree.component.ts#L94-L97" class="ngde">cancelEdit()</a> method which sets everything back to the way it was.</p><p class="ngde">When we press <code class="ngde">ENTER</code>, it triggers our <a href="https://github.com/DaveMBush/SmartNgRX/blob/main/apps/demo/src/app/shared/components/tree/tree.component.ts#L99-L105" class="ngde">saveNode()</a> method. The undoes the edit state of our presentation and then <a href="https://github.com/DaveMBush/SmartNgRX/blob/main/apps/demo/src/app/shared/components/tree/tree.component.ts#L103" class="ngde">stores the editingContent field into the node's name field</a>. Updating the node's name field optimistically updates the field and eventually triggers the Effects Service to update the server.</p>`,y=(()=>{let e=class e extends s{constructor(){super(),this.routePrefix="",this.pageType="guide",this.pageContent=R,this.page=t,this.demoAssets=u}};e.\u0275fac=function(n){return new(n||e)},e.\u0275cmp=r({type:e,selectors:[["ng-doc-page-demo-walkthrough-updating-fields"]],standalone:!0,features:[p([{provide:s,useExisting:e},l,t.providers??[]]),d,h],decls:1,vars:0,template:function(n,P){n&1&&c(0,"ng-doc-page")},dependencies:[g],encapsulation:2,changeDetection:0});let o=e;return o})(),L=[i(a({},(0,f.isRoute)(t.route)?t.route:{}),{path:"",component:y,title:"Updating Fields"})],T=L;export{y as DynamicComponent,T as default};
