import{a as d}from"./chunk-CG3MNAEL.js";import"./chunk-3FR5PQZO.js";import"./chunk-T4WX5D76.js";import{a as m}from"./chunk-CDOWFHT3.js";import{a as n}from"./chunk-LSIHCBJT.js";import"./chunk-PZEHRWLK.js";import{X as S}from"./chunk-CQLVC7LO.js";import{Qa as c,pb as l,ta as r,xa as g}from"./chunk-JNJDPWAB.js";import{a as i,b as a,h as y}from"./chunk-TWZW5B45.js";var h=y(S());var D={title:"Using With Existing Signals",mdFile:"./index.md",order:13,category:m},t=D;var p=[];var w={},u=w;var x=`<h1 id="using-with-existing-signals" href="using-smart-signals/using-with-existing-signals" headinglink="true" class="ngde">Using With Existing Signals<ng-doc-heading-anchor class="ng-doc-anchor ngde" anchor="using-with-existing-signals"></ng-doc-heading-anchor></h1><p class="ngde">Since the results of <code class="ngde ng-doc-code-with-link"><a href="internal-api/functions/internal-api/createSmartSignal" class="ng-doc-code-anchor ngde">createSmartSignal</a></code> and <code class="ngde ng-doc-code-with-link"><a href="internal-api/functions/internal-api/getTopChildRows" class="ng-doc-code-anchor ngde">getTopChildRows</a></code> return signals, they can be composed in other computed() signals to manage the data however you need. Just remember that for the magic to happen, you need to make sure to use the results of these two functions. Don't try to go after the core signals yourself. This is a limitation of how signals work.</p><p class="ngde">You can also use with existing observables, including NgRX Selectors, once you've converted the observable to a Signal using the Angular <code class="ngde">toSignal()</code> function.</p><ng-doc-blockquote type="note" class="ngde"><p class="ngde">If you want to combine existing selectors and signals to migrate from classic NgRX to signals, you will need to convert your selector dependencies from the bottom up. This is a limitation of how Selectors and Signals work. Selectors are, essentially RxJS operators and operate on an existing stream. Think of them as enhanced combineLatest() operators.</p><p class="ngde">Signals are not streams and, even if you convert them to RxJS streams, and make them look like selectors, they do not work correctly when passed into createSelector(). Oh, they may for a while, but it won't take long before you figure out that they don't, and can't work that way.</p><p class="ngde">You may be better off converting all your existing code to use SmartNgRX and then convert SmartNgRX to SmartSignals since the two Smart APIs are very similar to each other.</p></ng-doc-blockquote>`,e=class e extends n{constructor(){super();this.pageType="guide";this.pageContent=x;this.editSourceFileUrl="https://github.com/DaveMBush/SmartNgRX/edit/main/apps/documentation/src/app/using-smart-signals/using-with-existing-signals/index.md?message=docs(): describe your changes here...";this.viewSourceFileUrl="https://github.com/DaveMBush/SmartNgRX/blob/release/apps/documentation/src/app/using-smart-signals/using-with-existing-signals/index.md";this.page=t;this.demoAssets=u}};e.\u0275fac=function(o){return new(o||e)},e.\u0275cmp=r({type:e,selectors:[["ng-doc-page-9mnjuq4n"]],features:[l([{provide:n,useExisting:e},p,t.providers??[]]),g],decls:1,vars:0,template:function(o,R){o&1&&c(0,"ng-doc-page")},dependencies:[d],encapsulation:2,changeDetection:0});var s=e,N=[a(i({},(0,h.isRoute)(t.route)?t.route:{}),{path:"",component:s,title:"Using With Existing Signals"})],L=N;export{s as PageComponent,L as default};
