import{a as g}from"./chunk-NCOWMHCX.js";import{a as p}from"./chunk-FVRGWNNR.js";import"./chunk-VM5ESKQM.js";import{a as o}from"./chunk-C6HCTMWR.js";import{L as d,Qa as l,Ra as h,Rb as D,ia as r,va as c}from"./chunk-O5TFSMGJ.js";import{a,b as s,g as y}from"./chunk-JPKLQMV2.js";var u=y(D());var x={title:"ChildDefinition",mdFile:"./index.md",order:7,category:g},t=x;var f=[];var C={},m=C;var S=`<h1 id="childdefinition" class="ngde">ChildDefinition<a title="Link to heading" class="ng-doc-header-link ngde" href="/using-smart-ng-rx/child-definition#childdefinition"><ng-doc-icon icon="link-2" size="16" class="ngde"></ng-doc-icon></a></h1><p class="ngde">Now that we've setup our state management, the last step is being able to get the data out of the store and into our components where we can see them. This is where Smart Selectors come in.</p><p class="ngde">But before we can create a selector, we need to understand the <code class="ngde ng-doc-code-with-link" class="ngde"><a href="/api/smart-ngrx/interfaces/ChildDefinition" class="ng-doc-code-anchor ngde" data-link-type="Interface" class="ngde">ChildDefinition</a></code> interface. It has the following components, as outlined in the API documentation:</p><ul class="ngde"><li class="ngde"><p class="ngde">childFeature - The name of the feature the child entity was registered with. We need to supply this and the childFieldName so that the code can lookup information about the entity we've already supplied from <code class="ngde ng-doc-code-with-link" class="ngde"><a href="/api/smart-ngrx/functions/provideSmartFeatureEntities" class="ng-doc-code-anchor ngde" data-link-type="Function" class="ngde">provideSmartFeatureEntities</a></code>.</p></li><li class="ngde"><p class="ngde">childEntity - The name of the child entity.</p></li><li class="ngde"><p class="ngde">childSelector - the selector we use to retrieve the information for the child. The childSelector is an EntityState selector. This allows us to lookup an ID in the entity quickly.</p></li><li class="ngde"><p class="ngde">parentField - the name of the field in the parent entity that contains the IDs of the children.</p></li><li class="ngde"><p class="ngde">parentFeature - the feature the parent entity was registered with.</p></li><li class="ngde"><p class="ngde">parentEntity - the name of the parent entity.</p></li></ul><p class="ngde">We need the parentFeature and parentEntity so we can lookup the parent entity in the store when we need to add or delete an item. Since our parent item has the collection of IDs for the children, when we add or delete a child, we will need to add or remove the ID from the parent's collection of children.</p>`,P=(()=>{let e=class e extends o{constructor(){super(),this.routePrefix="",this.pageType="guide",this.pageContent=S,this.page=t,this.demoAssets=m}};e.\u0275fac=function(i){return new(i||e)},e.\u0275cmp=d({type:e,selectors:[["ng-doc-page-using-smart-ng-rx-child-definition"]],standalone:!0,features:[l([{provide:o,useExisting:e},f,t.providers??[]]),r,h],decls:1,vars:0,template:function(i,F){i&1&&c(0,"ng-doc-page")},dependencies:[p],encapsulation:2,changeDetection:0});let n=e;return n})(),v=[s(a({},(0,u.isRoute)(t.route)?t.route:{}),{path:"",component:P,title:"ChildDefinition"})],b=v;export{P as DynamicComponent,b as default};
